CREATE VIEW VIS_PCP_MATE_REFERENCIAS (CONS_MATE_REFERENCIA, CONS_MATE_GRUPO, 
    CODI_MATE_GRUPO, NOMB_MATE_GRUPO, CONS_MATE_SUBGRUPO, CODI_MATE_SUBGRUPO, 
