CONS_BODEGA INTEGER,
OTRA_UBICACION VARCHAR(60) CHARACTER SET NONE,
CONS_TURNO INTEGER,
CONS_PERS_CARGO INTEGER,
CONS_PERS_CENT_COSTO INTEGER,
CONS_COST_GRUPO INTEGER)
AS 


BEGIN

  FOR

