         CODI_PERSONAL,

         PRIM_APELLIDO,

         SEGU_APELLIDO,

         NOMBRES,

         NOMB_COMPLETO,

         CONS_PERS_TIPO_MOVIMIENTO,

         CONS_FAMI_MOVIMIENTO,

         CONS_ESTADO,

