        CONS_ABONADO, CONS_PROD_ORDEN, MINU_TRABAJADOS, MINU_ABONADOS,
        TIQUETES, VALO_HORA)
    VALUES (
        :CONS_CONT_NOVE_ABONADO, :CONS_CONT_NOVE_DIARIA, :CONS_ABONADO,
        :CONS_PROD_ORDEN, :MINU_TRABAJADOS, :MINU_ABONADOS,
        :TIQUETES, :VALO_HORA);
END ^

