    INSERT INTO PCP_REFE_FICH_07_MEDI_ESPECIAL (
        CONS_REFE_FICHA, IMAG_1, IMAG_1_SMALL)
    VALUES (
        :CONS_REFE_FICHA, :IMAG_1, :IMAG_1_SMALL);
END ^

